import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';
import { Router } from '@angular/router';
import { FormControl, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';

@Component({
  selector: 'seng41293-login',
  standalone: true,
  imports: [CommonModule,ReactiveFormsModule,MatInputModule,MatButtonModule],
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent {

  emailCTRL = new FormControl(null,[
      Validators.required,
      Validators.email
    ])
  loginFormGroup = new FormGroup({
    email : this.emailCTRL,
    password : new FormControl(null,Validators.required)
  })

  constructor(private router:Router){}

  onLogin(){
    console.log(this.loginFormGroup.valid);
    // this.router.navigate(['/admin']);
  }
}
<form class="login-form" [formGroup] = "loginFormGroup">
    <h2>Login</h2>
    <mat-form-field class="example-full-width" appearance="outline">
        <mat-label>Username</mat-label>
        <input matInput [formControl]="emailCTRL">
        <mat-error *ngIf="emailCTRL.hasError('email')">
            Please enter a valid email address
        </mat-error>
        <mat-error *ngIf="emailCTRL.hasError('required')">
            Email is <strong>required</strong>
        </mat-error>
    </mat-form-field>
    <mat-form-field class="example-full-width" appearance="outline">
        <mat-label>Password</mat-label>
        <input type="password" matInput formControlName="password">
        <mat-error *ngIf="loginFormGroup.password.hasError('email')">
            Please enter a valid email address
        </mat-error>
        <mat-error *ngIf="loginFormGroup.password.hasError('required')">
            Email is <strong>required</strong>
        </mat-error>
    </mat-form-field>
    <button mat-flat-button color="primary" [disabled] = "!loginFormGroup.valid" (click)="onLogin()">Login</button>
</form>
